import Foundation


public class ShelfItems {
  
  public init () { }
  
  public static func allItems() -> [ShelfItem] {
    return allShelfItems().sorted(by: {$0.name < $1.name})
  }
  
  //  public static func search( item: ShelfItem ) -> ShelfItem {
  //
  //  }
  
  public static func filterByItemType( type: String ) -> [ShelfItem] {
    let allItems = allShelfItems()
    let filteredItems = allItems.filter { $0.category == type }
    return filteredItems
  }
  
  public static func filterByItemName( name: String ) -> [ShelfItem] {
    let allItems = allShelfItems()
    let filteredItems = allItems.filter { $0.name.lowercased().range(of: name.lowercased()) != nil }
    return filteredItems
  }
  
